import React from "react";
import { NavLink } from "react-router-dom";
import "./header.css"
const Header = () => {
  var prevScrollPos = window.pageYOffset;
let arrowup = document.getElementById("arrow-up")
window.onscroll = function () {
  var currentScrollPos = window.pageYOffset;
  document.getElementById("header").style.background = " #b73604"
  // Scroll down 
  if (prevScrollPos < currentScrollPos) {

    document.getElementById("header").classList.add("header-visible");
    arrowup.style.bottom = "10%"
    document.getElementById("header").style.backgroundColor = "#b73604"

  }
  else if (prevScrollPos <= 10) {
    arrowup.style.bottom = "100%"
    document.getElementById("header").style.background = "transparent"
  }
  // Scroll up 
  else {
    document.getElementById("header").classList.add("header-visible");
    document.getElementById("header").classList.remove("header-hidden");

  }
  prevScrollPos = currentScrollPos;
}
  return (
    <header>

      <div className="container">
        <div className="row">
          <div className="border">
          </div>
          <div className="content flex">
            <div className="logo flex">
              <div className="image">
                <img src="../../../Image/logo.png" alt="" />
              </div>
            </div>
            <div className="menu">
              <ul className="flex" onClick={() => {
                let clicksound = new Audio("https://uploads.sitepoint.com/wp-content/uploads/2023/06/1687569402mixkit-fast-double-click-on-mouse-275.wav");
                clicksound.play();
              }}>
                <li><NavLink to="/">Home</NavLink></li>
                <li><NavLink to="./about">About</NavLink></li>
                <li><NavLink to="./contact">Contact</NavLink></li>
                <li> <NavLink to="./gallery">Pages</NavLink></li>
                <li> <NavLink to="/ragistration">Jobs</NavLink></li>
              </ul>
            </div>
            <div className="btn flex">
              <NavLink to="/login" onClick={() => {
                let clicksound = new Audio("https://uploads.sitepoint.com/wp-content/uploads/2023/06/1687569402mixkit-fast-double-click-on-mouse-275.wav");
                clicksound.play();
              }}>Login</NavLink>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}
export default Header;
