import React, { useEffect, useState } from "react";
import { DataMenu } from "../../Apimenu";
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { cartActions } from "../../store/card";
const Offer = () => {
  const cartItems = useSelector((state) => state.cart.itemsList);
 const quantity=useSelector((state)=>state.cart.totleQuantity);
 console.log();
 
  const [record, setRecord] = useState([]);
  // const [CartValue, setCartValue] = useState(0);
  const [LikedValue, setLikedValue] = useState(0);
  const dispatch = useDispatch();
  const AddItem = (val) => {
    const name = val.name;
    const id = val.id;
    const price = val.price;
    dispatch(cartActions.addToCart({
      name,
      id,
      price,
    }))
    // setCartValue(CartValue + 1)
  }
  const Likeditem = () => {

    setLikedValue(LikedValue + 1)
  }
  useEffect(() => {
    const filterData = DataMenu.filter((data) => {
      return "SpecialDish" == data.category
    })
    setRecord(filterData);
  }, [setRecord])

  return (
    <>
      <section className="offer-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="buy">
              <NavLink to="/cartlist"><button><h5>{quantity}</h5><i class="fa-solid fa-cart-shopping"></i></button></NavLink>
            </div>
            <div className="like-btn">
              <button><h5>{LikedValue}</h5><i class="fa-solid fa-heart"></i></button>
            </div>
            <div className="content">
              <div className="heading">
                <span>today offer</span>
                <h1>special Items</h1>
              </div>
              <div className="main-cart flex">
                {
                  record && record.map((val) => {
                    const { id, price, name, image, category } = val;

                    return (
                      <div className="cart">
                        <div className="content" key={id}>
                          <div className="image">
                            <div className="title">
                              <h5>availabel</h5>
                            </div>
                            <div className="like">
                              <button onClick={Likeditem}><i className="fa-solid fa-heart"></i></button>
                            </div>
                            <div className="addtocart">
                              <button onClick={() => AddItem(val)}>Add to cart</button>
                            </div>
                            <div className="btn flex">
                              <NavLink to={`/addtocart/${id}`}>Order</NavLink>
                            </div>
                            <img src={image} alt="" />
                          </div>
                          <div className="details flex">

                            <ul className="flex">
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                            </ul>
                            <p>{name}</p><span>â‚¹{price}</span>
                          </div>
                        </div>
                      </div>
                    )
                  })
                }
              </div>
            </div>
          </div>
        </div>
      </section>

    </>
  );
}
export default Offer;
