import React, { useEffect } from "react";
import { NavLink } from "react-router-dom";
const RastaurantImage = () => {

  function imagechange() {
    var cards = document.querySelectorAll('.main-card .card');
    var images = document.querySelectorAll('.main-card .image-bg img')
    console.log(images);
    cards.forEach(function (card, index) {
      card.addEventListener('mouseover', function () {
        // Remove active class from all images
        images.forEach(function (image) {
          image.classList.remove('active');
        });
        // Add active class to the corresponding image
        images[index].classList.add('active');
      });
    });
  }
  return (
    <>
      <section className="rastaurantimage-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="content">
              <div className="main-card">
                <div className="image-bg">
                  <img src="../../../Image/AdobeStock_98997233_Preview.jpeg" alt="" className="active" />
                  <img src="../../../Image/AdobeStock_865484385_Preview.jpeg" alt="" />
                  <img src="../../../Image/AdobeStock_889665166_Preview.jpeg" alt="" />
                  <img src="../../../Image/AdobeStock_627853212_Preview.jpeg" alt="" />
                  <img src="../../../Image/AdobeStock_734623462_Preview.jpeg" alt="" />
                </div>
                <div className="card" onMouseOver={imagechange()}>
                  <div className="content">
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
                <div className="card" onMouseOver={imagechange()}>
                  <div className="content">
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
                <div className="card" onMouseOver={imagechange()}>
                  <div className="content">
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
                <div className="card" onMouseOver={imagechange()}>
                  <div className="content">
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
                <div className="card" onMouseOver={imagechange()}>
                  <div className="content">
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default RastaurantImage;
