import React, { useEffect } from "react";
import "./cartlist.css"
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { createAction } from "@reduxjs/toolkit";
import { cartActions } from "../../store/card";
const Cartlist = () => {
  const finalprice = 0;
  const dispatch = useDispatch();
  const cartItem = useSelector((state) => state.cart.itemsList);
  const Increment = (val) => {
    const name = val.name;
    const id = val.id;
    const price = val.price;
    const image = val.image;
    const category = val.category;
    dispatch(
      cartActions.addToCart(
        {
          name,
          id,
          price,
          image,
          category,
        }
      )
    )
  }
  const Decrement = (val) => {
    dispatch(cartActions.removeFromCart(val.id));
  }
  useEffect(() => {
    const totle = 0;    cartItem.forEach(item => {
      finalprice += item.totleprice;
    })
  }, [Increment, Decrement])
  return (
    <>
      <section className="cartlist-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="content">
              <div className="main-box">
                <div className="box">
                <p>{finalprice}</p>
                  {cartItem.map((val) => {
                    const { id, name, quantity, totleprice, price, image, category } = val;

                    return (
                      <>
                        <p>{name}</p>
                        <p>{quantity}</p>
                        <p>{totleprice}</p>
                        <p>{id}</p>
                      
                        <p>{price}</p>
                        <p>{category}</p>
                        <img src={image} alt="" />
                        <button onClick={() => Increment(val)}>+</button>
                        <button onClick={() => Decrement(val)}>-</button>
                      </>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default Cartlist;
