import React, { useState } from "react";

const RestaurantImage = () => {
  // State to keep track of the index of the active image
  const [activeIndex, setActiveIndex] = useState(0);

  // Handle mouse over event to update the active image index
  const handleMouseOver = (index) => {
    setActiveIndex(index);
  };

  return (
    <section className="restaurantimage-section">
      <div className="container">
        <div className="row">
          <div className="border flex">
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
          </div>
          <div className="content">
            <div className="main-card">
              <div className="image-bg">
                {/* Use activeIndex to determine which image has the 'active' class */}
                <img
                  src="../../../Image/AdobeStock_98997233_Preview.jpeg"
                  alt=""
                  className={activeIndex === 0 ? "active" : ""}
                />
                <img
                  src="../../../Image/AdobeStock_865484385_Preview.jpeg"
                  alt=""
                  className={activeIndex === 1 ? "active" : ""}
                />
                <img
                  src="../../../Image/AdobeStock_889665166_Preview.jpeg"
                  alt=""
                  className={activeIndex === 2 ? "active" : ""}
                />
                <img
                  src="../../../Image/AdobeStock_627853212_Preview.jpeg"
                  alt=""
                  className={activeIndex === 3 ? "active" : ""}
                />
                <img
                  src="../../../Image/AdobeStock_734623462_Preview.jpeg"
                  alt=""
                  className={activeIndex === 4 ? "active" : ""}
                />
              </div>
              {/* Render cards with onMouseOver event */}
              {[0, 1, 2, 3, 4].map((index) => (
                <div
                  key={index}
                  className="card"
                  onMouseOver={() => handleMouseOver(index)}
                >
                  <div className="content">
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default RestaurantImage;
