import React from "react";
import "./cartlist.css"
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { createAction } from "@reduxjs/toolkit";
import { cartActions } from "../../store/card";
const Cartlist = () => {
  const dispatch = useDispatch();
  let FinalPrice = 0;
  const cartItem = useSelector((state) => state.cart.itemsList);
  cartItem.forEach(element => {
    FinalPrice += element.totleprice;
  });
  const Increment = (val) => {
    const name = val.name;
    const id = val.id;
    const price = val.price;
    const image = val.image;
    const category = val.category;
    dispatch(
      cartActions.addToCart(
        {
          name,
          id,
          price,
          image,
          category,
        }
      )
    )
  }
  const Decrement = (val) => {
    dispatch(cartActions.removeFromCart(val.id));
  }
  return (
    <>
      <section className="cartlist-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="content">
              <div className="main-box">
                {cartItem.map((val) => {
                  const { id, name, quantity, totleprice, price, image, category } = val;
                  return (
                    <div className="box">
                      <div className="content">
                        <div className="image">
                          <img src={image} alt="" />
                        </div>
                        <div className="details">
                        <h5>{name}</h5>
                        </div>
                       
                        <p>{quantity}</p>
                        <p>{totleprice}</p>
                        
                        <p>{price}</p>
                        <p>{category}</p>

                        <button onClick={() => Increment(val)}>+</button>
                        <button onClick={() => Decrement(val)}>-</button>

                      </div>
                    </div>

                  );
                })}

              </div>
              <p>{FinalPrice}</p>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default Cartlist;
