import React from "react";
import "./home.css"
import { Punjabi, Roti, Gujrati, Chinese } from "./Apimenu";
import { NavLink } from "react-router-dom";
const Ourmenu = () => {
  return (
    <>
      <section className="Ourmenu-section">
        <div className="container">
          <div className="row">
            <div className="border">
            </div>
            <div className="content">
              <div className="heading">
                <span>special selection</span>
                <h2>our menu</h2>
              </div>
              <div className="main-box">
                <div className="box">
                  {/* <div className="btn">
                    <ul>
                      <li><button className="active" onClick={show}>punjabi</button></li>
                      <li><button onClick={show}>chinese</button></li>
                      <li><button onClick={show}>roti</button></li>
                      <li><button onClick={show}>gujrati</button></li>
                    </ul>
                  </div> */}
                  <ul className="punjabi flex">
                    {
                      Punjabi.map((data) => {
                        const { id, name, price } = data
                        return (
                          <li key={id}><NavLink><h2>{name}</h2><span>{price}</span></NavLink></li>
                        )
                      })
                    }
                  </ul>
                  <ul className="chinese">
                    {
                      Chinese.map((data) => {
                        const { id, name, price } = data
                        return (
                          <li key={id}><NavLink><h2>{name}</h2><span>{price}</span></NavLink></li>
                        )
                      })
                    }
                  </ul>
                  <ul className="roti">
                    {
                      Roti.map((data) => {
                        const { id, name, price } = data
                        return (
                          <li key={id}><NavLink><h2>{name}</h2><span>{price}</span></NavLink></li>
                        )
                      })
                    }
                  </ul>
                  <ul className="gujrati">
                    {
                      Gujrati.map((data) => {
                        const { id, name, price } = data
                        return (
                          <li key={id}><NavLink><h2>{name}</h2><span>{price}</span></NavLink></li>
                        )
                      })
                    }
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default Ourmenu;
