import React, { useEffect, useState } from "react";
import "./home.css"
import { DataMenu } from "./Apimenu";
import { NavLink } from "react-router-dom";
const Ourmenu = () => {
  const [BtnData, setBtnData] = useState('punjabi');
  const [actualdata, setactualdata] = useState();
  const active = (e) => {
    setBtnData(e.target.innerHTML)
   console.log(BtnData);
   console.log(actualdata);  
  }
  useEffect(() => {
    DataMenu.filter((data) => {
      if (BtnData == data.tag) {
        setactualdata(data);
      }
    })
  },[BtnData])

  return (
    <>
      <section className="Ourmenu-section">
        <div className="container">
          <div className="row">
            <div className="border">
            </div>
            <div className="content">
              <div className="heading">
                <span>special selection</span>
                <h2>our menu</h2>
              </div>
              <div className="main-box">
                <div className="btn">
                  <ul className="flex">
                    <li className="flex"><button className="active" onClick={active}>punjabi</button></li>
                    <li className="flex"><button onClick={active}>chinese</button></li>
                    <li className="flex"><button onClick={active}>roti</button></li>
                    <li className="flex"><button onClick={active}>gujrati</button></li>
                  </ul>
                </div>
                <div className="box">                
                  <ul className="flex">
                     {                  
                      actualdata && <li key={actualdata.id}>
                        <div className="title flex">
                          <h2>{actualdata.name}</h2><span>â‚¹{actualdata.price}</span>
                          <p>{actualdata.paragraph}</p>
                        </div>
                      </li> 
                     }
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default Ourmenu;
