import React, { useEffect, useRef, useState } from "react";

const RastaurantImage = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const cardsRef = useRef([]);
  const imagesRef = useRef([]);

  const handleMouseOver = (index) => {
    setActiveIndex(index);
  };

  useEffect(() => {
    const handleMouseOverEvent = (index) => () => handleMouseOver(index);

    // Attach event listeners after component mounts
    cardsRef.current.forEach((card, index) => {
      if (card) {
        card.addEventListener('mouseover', handleMouseOverEvent(index));
      }
    });

    // Clean up event listeners
    return () => {
      cardsRef.current.forEach((card, index) => {
        if (card) {
          card.removeEventListener('mouseover', handleMouseOverEvent(index));
        }
      });
    };
  }, []);

  return (
    <section className="rastaurantimage-section">
      <div className="container">
        <div className="row">
          <div className="border flex">
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
          </div>
          <div className="content">
            <div className="heading flex">
              <span>our restaurant images</span>
              <div className="title flex">
                <div className="image">
                  <img src="../../Image/logo.png" alt="" />
                </div>
                <h1>Ma Annapurana</h1>
                <div className="image">
                  <img src="../../Image/logo.png" alt="" />
                </div>
              </div>
              <p>We started this restaurant on the date <span>14-11-2004</span> and still, this is the best restaurant in Gujarat. Thank you for your support and love for Ma Annapurana restaurant.</p>
            </div>
            <div className="main-card">
              <div className="image-bg">
                {imagesRef.current.map((image, index) => (
                  <img
                    key={index}
                    src={`../../../Image/AdobeStock_${index + 1}_Preview.jpeg`}
                    alt=""
                    className={activeIndex === index ? "active" : ""}
                    ref={(el) => (imagesRef.current[index] = el)}
                  />
                ))}
              </div>
              {[0, 1, 2, 3, 4].map((index) => (
                <div
                  key={index}
                  className="card"
                  ref={(el) => (cardsRef.current[index] = el)}
                >
                  <div className="content">
                    <div className="image flex">
                      <img src="../../../Image/logo.png" alt="" />
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default RastaurantImage;
