import React, { useEffect, useState } from "react";
import { DataMenu } from "../../Apimenu";
import { NavLink } from "react-router-dom";
const Offer = () => {
  const [record, setRecord] = useState([]);
  useEffect(() => {
    const filterData = DataMenu.filter((data) => {
      return "SpecialDish" == data.category
    })
    setRecord(filterData);
  }, [setRecord])

  return (
    <>
      <section className="offer-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="content">
              <div className="heading">
                <span>Annapurana tasty offer</span>
                <h1>our menu</h1>
              </div>
              <div className="main-cart flex">
              <div class="marquee">
              <div class="marquee-inner">
                <img src="./Image/AdobeStock_228280764_Preview.jpeg" alt="" />
                <img src="./Image/AdobeStock_274091886_Preview.jpeg" alt="" />
                <img src="./Image/AdobeStock_294912254_Preview.jpeg" alt="" />
                <img src="./Image/AdobeStock_446859522_Preview.jpeg" alt="" />
                <img src="./Image/AdobeStock_647347899_Preview.jpeg" alt="" />
                <img src="./Image/AdobeStock_647349442_Preview.jpeg" alt="" />
                <img src="./Image/inner-pages-gallery-img-8.jpg" alt="" />
              </div>
            </div>
               


              </div>
            </div>
          </div>
        </div>
      </section>
      
    </>
  );
}
export default Offer;
