import React, { useEffect, useState } from "react";
import "./addtocart.css";
import { DataMenu } from "../../Apimenu";
import { NavLink, useParams } from "react-router-dom";
const Addtocart = () => {
  const { id } = useParams();
  const [data, setData] = useState();
  const [incMoney, setIncmoney] = useState();
  const [incItem, setIncitem] = useState(1)
  const incricebtn = (price) => {
    incMoney=
    setIncitem(incItem + 1);
    setIncmoney(incMoney+price)
    console.log(incMoney)
  }
  const decricebtn = (price) => {
    console.log(price);
    setIncitem(incItem - 1);
    setIncmoney(incMoney-price)
  }
  useEffect(() => {
    const Filterdata = DataMenu.filter((val) => {
      return val.id == id
    })
    setData(Filterdata);
  }, [id])
  return (
    <>
      <section className="addtocart-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="content">
              <div className="main-box flex">

                {
                  data && data.map((val) => {
                    const { id, name, image, price } = val;
                    return (
                      <div className="box" key={id}>

                        <div className="content flex">
                          <div className="image">
                            <img src={image} alt="product-photo" />
                          </div>
                          <div className="details">
                            <marquee behavior="scroll" direction="left">hurry up order fast before food is out off stock</marquee>
                            <div className="title flex">
                              <h5>{name}</h5>
                              <span>₹{price}</span>
                            </div>
                            <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. At nostrum laudantium consequuntur dolorem. Quibusdam atque dolorum quas suscipit quidem quia deserunt aperiam laudantium ad? Ipsa quasi omnis modi laudantium corporis?</p>
                            <div className="btn">
                              <ul className="flex">
                                <li className="flex"><button onClick={() => decricebtn(price)}>-</button><h5>{incItem}</h5><button onClick={() => incricebtn(price)}>+</button></li>
                                <li><NavLink><button>pay:-{price}</button></NavLink></li>
                                {incMoney}
                              </ul>
                            </div>
                          </div>
                        </div>

                      </div>
                    );

                  })

                }
              </div>

            </div>
          </div>
        </div>
      </section>
    </>
  );
}
export default Addtocart;
