import React from "react";
import { NavLink } from "react-router-dom";
// import "./home.css"
const Itemslider = () => {
  document.addEventListener("DOMContentLoaded", () => {
    const marquee = document.querySelector(".marquee-inner");
    const speed = 1; // Scrolling Speed
    let scrollAmount = 0;
    let isHovered = false;
  
    // Duplicates the content
    const marqueeContent = marquee.innerHTML;
    marquee.innerHTML += marqueeContent;
  
    const startScrolling = () => {
      if (!isHovered) {
        scrollAmount -= speed;
        if (Math.abs(scrollAmount) >= marquee.scrollWidth / 2) {
          scrollAmount = 0;
        }
        marquee.style.transform = `translateX(${scrollAmount}px)`;
      }
      requestAnimationFrame(startScrolling);
    };
  
    marquee.addEventListener("mouseover", () => {
      isHovered = true;
    });
  
    marquee.addEventListener("mouseout", () => {
      isHovered = false;
    });
  
    startScrolling();
  });
  return (
    <>
      <div class="marquee">
                <div class="marquee-inner">
                  <img src="./Image/AdobeStock_228280764_Preview.jpeg" alt="" />
                  <img src="./Image/AdobeStock_493064712_Preview.jpeg" alt="" />
                  <img src="./Image/AdobeStock_274091886_Preview.jpeg" alt="" />
                  <img src="./Image/AdobeStock_294912254_Preview.jpeg" alt="" />
                  <img src="./Image/AdobeStock_308622058_Preview.jpeg" alt="" />
                  <img src="./Image/AdobeStock_322495633_Preview.jpeg" alt="" />
                </div>
              </div>
    </>
  );
}
export default Itemslider;
