import React, { useEffect, useRef, useState } from "react";
import { NavLink } from "react-router-dom";

const RastaurantImage = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const cardsRef = useRef([]);
  const imagesRef = useRef([]);

  const handleMouseOver = (index) => {
    setActiveIndex(index);
  };

  useEffect(() => {
    // Clean up event listeners
    return () => {
      cardsRef.current.forEach(card => {
        card.removeEventListener('mouseover', handleMouseOver);
      });
    };
  }, []);

  useEffect(() => {
    // Attach event listeners after component mounts
    cardsRef.current.forEach((card, index) => {
      const handleMouseOverEvent = () => handleMouseOver(index);
      card.addEventListener('mouseover', handleMouseOverEvent);
      return () => card.removeEventListener('mouseover', handleMouseOverEvent);
    });
  }, [cardsRef]);

  return (
    <section className="rastaurantimage-section">
      <div className="container">
        <div className="row">
          <div className="border flex">
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
            <div className="sub-border"></div>
          </div>
          <div className="content">
            <div className="main-card">
              <div className="image-bg">
                <img
                  src="../../../Image/AdobeStock_98997233_Preview.jpeg"
                  alt=""
                  className={activeIndex === 0 ? "active" : ""}
                  ref={(el) => (imagesRef.current[0] = el)}
                />
                <img
                  src="../../../Image/AdobeStock_811058688_Preview.jpeg"
                  alt=""
                  className={activeIndex === 1 ? "active" : ""}
                  ref={(el) => (imagesRef.current[1] = el)}
                />
                <img
                  src="../../../Image/AdobeStock_889665166_Preview.jpeg"
                  alt=""
                  className={activeIndex === 2 ? "active" : ""}
                  ref={(el) => (imagesRef.current[2] = el)}
                />
                <img
                  src="../../../Image/AdobeStock_627853212_Preview.jpeg"
                  alt=""
                  className={activeIndex === 3 ? "active" : ""}
                  ref={(el) => (imagesRef.current[3] = el)}
                />
                <img
                  src="../../../Image/AdobeStock_734623462_Preview.jpeg"
                  alt=""
                  className={activeIndex === 4 ? "active" : ""}
                  ref={(el) => (imagesRef.current[4] = el)}
                />
              </div>
              {[0, 1, 2, 3, 4].map((index) => (
                <div
                  key={index}
                  className="card"
                  ref={(el) => (cardsRef.current[index] = el)}
                >
                  <div className="content">
                  <div className="image flex">
                    <img src="../../../Image/logo.png" alt="" />
                  </div>
                    <i className="fa-solid fa-fan"></i>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default RastaurantImage;
