import React, { useEffect, useState } from "react";
import { DataMenu } from "../../Apimenu";
import { NavLink } from "react-router-dom";
const Offer = () => {
  const [record, setRecord] = useState([]);
  useEffect(() => {
    const filterData = DataMenu.filter((data) => {
      return "SpecialDish" == data.category
    })
    setRecord(filterData);
  }, [setRecord])

  return (
    <>
      <section className="offer-section">
        <div className="container">
          <div className="row">
            <div className="border flex">
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
              <div className="sub-border"></div>
            </div>
            <div className="content">
              <div className="heading">
                <span>Annapurana tasty offer</span>
                <h1>our menu</h1>
              </div>
              <div className="main-cart flex">
              <div class="marquee">
              <div class="marquee-inner">
              {
                  record && record.map((val) => {
                    const { id, price, name, image, category } = val
                    return (
                      <div className="cart">
                        <div className="content" key={id}>
                          <div className="image">
                            <div className="btn flex">
                              <NavLink to={`/addtocart/${id}`}>Order</NavLink>
                            </div>
                            <img src={image} alt="" />
                          </div>
                          <div className="details flex">
                            <h5>{category}</h5>
                            <ul className="flex">
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                              <li><i class="fa-solid fa-star"></i></li>
                            </ul>
                            <p>{name}</p><span>â‚¹{price}</span>
                          </div>
                        </div>
                      </div>
                    )
                  })
                }
              </div>
            </div>
               


              </div>
            </div>
          </div>
        </div>
      </section>
      
    </>
  );
}
export default Offer;
